import * as actionTypes from "./actionTypes";
import axios from "axios";

export const authStart = () => {
  return {
    type: actionTypes.AUTH_START,
  };
};

export const authSuccess = (authData, idToken, userId) => {
  return {
    type: actionTypes.AUTH_SUCCESS,
    authData: authData,
    idToken: idToken,
    uderId: userId,
  };
};
export const authFail = (error) => {
  return {
    type: actionTypes.AUTH_FAIL,
    error: error,
  };
};

export const auth = (email, password, isSignUp) => {
  return (dispatch) => {
    dispatch(authStart());
    const authData = {
      email: email,
      password: password,
      returnSecureToken: true,
    };
    let url =
      "https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyCT1oAkg5SYkgIQaNRoxOW8xTpq2EaFujY";
    if (!isSignUp) {
      url =
        "https://identitytoolkit.googleapis.com/v1/accounts:signInWithPassword?key=AIzaSyCT1oAkg5SYkgIQaNRoxOW8xTpq2EaFujY";
    }
    axios
      .post(url, authData)
      .then((response) => {
        console.log(response);
        dispatch(
          authSuccess(
            response.data,
            response.data.idToken,
            response.data.userId
          )
        );
      })
      .catch((err) => {
        console.log(err);
        dispatch(authFail(err));
      });
  };
};
