import * as actionTypes from "./actions";

const initialState = {
  ingredients: {
    Salad: 0,
    Bacon: 0,
    Cheese: 0,
    Meat: 0,
  },
  totalPrice: 4,
  durationTime: 0,
  perchasable: false,
  // purchasing: false,
  // loading: false,
  // error: false,
};
const INGREDIENT_PRICES = {
  Salad: 0.5,
  Cheese: 0.5,
  Bacon: 1,
  Meat: 2,
};

const deliveryDurationCalculator = (ingredients) => {
  let totalIngredients = 0;
  let duration = null;
  // for (const ingredient in ingredients) {
  //   totalIngredients += ingredients[ingredient];
  // }
  totalIngredients = Object.values(ingredients).reduce((acc, curr) => {
    return acc + Number(curr);
  }, 0);

  if (totalIngredients >= 1) duration = 15;
  if (totalIngredients >= 4) duration = 20;
  if (totalIngredients >= 6) duration = 30;
  if (totalIngredients >= 10) duration = 60;
  return duration;
};

const updatePerchaseState = (ingredients) => {
  const sum = Object.values(ingredients).reduce((acc, curr) => {
    return acc + curr;
  }, 0);
  return { perchasable: sum > 0 };
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.ADD_INGREDIENT:
      return {
        ...state,
        ingredients: {
          ...state.ingredients,
          [action.ingredientName]: state.ingredients[action.ingredientName] + 1,
        },
        totalPrice: state.totalPrice + INGREDIENT_PRICES[action.ingredientName],
        durationTime: deliveryDurationCalculator(state.ingredients),
        perchasable: updatePerchaseState(state.ingredients),
      };
    case actionTypes.REMOVE_INGREDIENT:
      return {
        ...state,
        ingredients: {
          ...state.ingredients,
          [action.ingredientName]: state.ingredients[action.ingredientName] - 1,
        },
        totalPrice: state.totalPrice - INGREDIENT_PRICES[action.ingredientName],
        durationTime: deliveryDurationCalculator(state.ingredients),
        perchasable: updatePerchaseState(state.ingredients),
      };
    default:
      return state;
  }
};

export default reducer;
