import { stat } from "fs-extra";
import * as actionTypes from "./actions";

const initialState = {
  ingredients: null,
  totalPrice: 4,
  // durationTime: 0,
  // perchasable: false,
  // purchasing: false,
  // loading: false,
  // error: false,
};

const reducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.ADD_INGREDIENT:
      return {};
    case actionTypes.REMOVE_INGREDIENT:
      return {};
    default:
      return state;
  }
};

export default reducer;
