import React, { Component } from "react";
import axios from "../../axios-orders";
import withErrorHandler from "../../hoc/withErrorHandler/withErrorHandler";
import Order from "../../components/Order/Order";
import Spinner from "../../components/UI/Spinner/Spinner";
import * as actions from "../../store/actions/index";
import { connect } from "react-redux";
import { Redirect } from "react-router";
class Orders extends Component {
  componentDidMount() {
    if (this.props.token) this.props.onFetchOrders(this.props.token);
  }
  render() {
    return (
      <div>
        {!this.props.loading && localStorage.getItem("token") ? (
          this.props.orders.map((el) => {
            return (
              <Order
                key={el.id}
                ingredients={el.ingredients}
                price={el.price}
                customer={el.orderData}
                deliveryDuration={el.duration}
              />
            );
          })
        ) : this.props.loading ? (
          <Spinner />
        ) : (
          <Redirect to="/login" />
        )}
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  orders: state.order.orders,
  loading: state.order.loading,
  token: state.auth.token,
});

const mapDispatchToProps = (dispatch) => ({
  onFetchOrders: (token) => dispatch(actions.fetchedOrders(token)),
});
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(withErrorHandler(Orders, axios));
