import * as actionTypes from "./actions";

const initialState = {
  ingredients: {
    Salad: 0,
    Bacon: 0,
    Cheese: 0,
    Meat: 0,
  },
  totalPrice: 4,
  perchasable: false,
  // purchasing: false,
  // loading: false,
  // error: false,
};
const INGREDIENT_PRICES = {
  Salad: 0.5,
  Cheese: 0.5,
  Bacon: 1,
  Meat: 2,
};
const deliveryDurationCalculator = (ingredients) => {
  let totalIngredients = null;
  let duration = 0;
  totalIngredients = Object.values(ingredients).reduce((acc, curr) => {
    acc += Number(curr);
    return acc;
  }, 0);
  if (totalIngredients >= 1) duration = 15;
  if (totalIngredients >= 4) duration = 20;
  if (totalIngredients >= 6) duration = 30;
  if (totalIngredients >= 10) duration = 60;
  return duration;
};

const reducer = (state = initialState, action) => {
  const duration = deliveryDurationCalculator(state.ingredients);
  switch (action.type) {
    case actionTypes.ADD_INGREDIENT:
      return {
        ...state,
        ingredients: {
          ...state.ingredients,
          [action.ingredientName]: state.ingredients[action.ingredientName] + 1,
        },
        totalPrice: state.totalPrice + INGREDIENT_PRICES[action.ingredientName],
        durationTime: duration,
      };
    case actionTypes.REMOVE_INGREDIENT:
      return {
        ...state,
        ingredients: {
          ...state.ingredients,
          [action.ingredientName]: state.ingredients[action.ingredientName] - 1,
        },
        totalPrice: state.totalPrice - INGREDIENT_PRICES[action.ingredientName],
        durationTime: duration,
      };

    default:
      return state;
  }
};

export default reducer;
