import React, { Component } from "react";
import Button from "../../../components/UI/Button/Button";
import classes from "./ContactData.css";
import axios from "../../../axios-orders";
import Spinner from "../../../components/UI/Spinner/Spinner";
import Input from "../../../components/UI/Input/Input";
class ContactData extends Component {
  // name: "",
  //   email: "",
  //   address: {
  //     street: "",
  //     postalCode: "",
  //   },
  state = {
    orderForm: {
      name: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Your Name",
        },
        value: "",
      },
      street: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Street",
        },
        value: "",
      },
      zipCode: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "ZIP CODE",
        },
        value: "",
      },
      country: {
        elementType: "input",
        elementConfig: {
          type: "text",
          placeholder: "Country",
        },
        value: "",
      },
      email: {
        elementType: "input",
        elementConfig: {
          type: "email",
          placeholder: "Your Email",
        },
        value: "",
      },
      deliveryDuration: "60 minutes",
    },
    loading: false,
  };
  deliveryDurationHandler = () => {
    let totalIngredients = 0;
    for (const ingredient in this.props.ingredients) {
      totalIngredients += this.props.ingredients[ingredient];
    }
    if (totalIngredients >= 10) return 60;
    if (totalIngredients >= 6) return 30;
    if (totalIngredients >= 4) return 20;
    if (totalIngredients >= 1) return 15;
  };
  orderHandler = (event) => {
    event.preventDefault();
    this.setState({ loading: true });
    const order = {
      ingredients: this.props.ingredients,
      price: this.props.price,
    };
    axios
      .post("/orders.json", order)
      .then((response) => {
        this.setState({ loading: false });
        this.props.history.push("/");
      })
      .catch((error) => {
        this.setState({ loading: false });
      });
  };
  render() {
    console.log(this.props);
    let form = null;
    if (this.state.loading) {
      form = <Spinner />;
    } else {
      form = (
        <form>
          <Input
            inputtype="input"
            type="text"
            name="name"
            placeholder="Your name"
          />
          <Input
            inputtype="input"
            type="email"
            name="email"
            placeholder="Your email"
          />
          <Input
            inputtype="input"
            type="text"
            name="street"
            placeholder="Your street"
          />
          <Input
            inputtype="input"
            type="text"
            name="postal"
            placeholder="Your postal code"
          />
          <Button btnType="Success" clicked={this.orderHandler}>
            ORDER
          </Button>
        </form>
      );
    }
    return (
      <div className={classes.ContactData}>
        <h4>Enter your Contact Data</h4>
        {form}
      </div>
    );
  }
}
export default ContactData;
